func rootpage(w http.ResponseWriter, req *http.Request) {
	// get rid of :port
	host := req.Host
	if i := strings.Index(host, ":"); i != -1 {
		host = host[:i]
	}

	templ.Execute(w, struct {
		KodiURL string
	}{
		KodiURL: "http://" + host + ":" + strconv.Itoa(*kodiPort),
	})
}

